import React, { Component} from 'react'
import PlantList from "./PlantList/PlantList"
import PlantNav from "./nav"
import {
    Card,
    CardHeader,
    CardTitle,
    CardImg,
    CardBody,
    CardFooter,
    Button
  } from "shards-react";


class MainContainer extends Component {
    constructor() {
        super();
        this.state = {
            clients: []
        }
    }
    componentDidMount(){
        this.getClients();
    }
    getClients = async () => {
        const clients = await fetch("http://localhost:3001/clients");
        const parsedResponse = await clients.json()
        console.log(parsedResponse);
        this.setState({
            clients: parsedResponse
        })
    }
    render(){
        const clients = this.state.clients.map((clients)=>{
            return <div key={clients.id}> 
                {/* <h3>{clients.first_name} {clients.last_name}</h3> */}
                <Card style={{ maxWidth: "300px" }, {small: true}}>
                <CardHeader>{clients.first_name} {clients.last_name}</CardHeader>
                <CardImg src="https://www.clarifybd.com/wp-content/uploads/2016/03/client-icon-blue.png" />
                <CardBody>
                    <CardTitle>Last contact: {clients.last_contact}</CardTitle>
                    <p>Last contact: {clients.last_contact}</p>
                    <Button>Read more &rarr;</Button>
                </CardBody>
                <CardFooter>Card footer</CardFooter>
                </Card>
            </div>
        })
        return(
            <div class="clientList">
                <PlantNav />
                <p class="dash">ClientKeeper Dashboard :)</p>
                <br></br>
                {clients}
            </div>
        )
    }
}
